package zy.leetcode.string.solution214sec;

import org.junit.Assert;
import org.junit.Test;

public class Solution {
    public String shortestPalindrome(String s) {
        int n = s.length();
        int mid = n/2;
        int reverse = -1;
        if(n%2 == 0){
            boolean isAA = true;
            while(mid >= 0){
                if(isAA){
                    reverse = doCheck(s, mid, mid+1);
                    isAA = false;
                }else{
                    reverse = doCheck(s, mid-1, mid+1);
                    mid--;
                    isAA = true;
                }
                if(reverse >= 0) break;
            }
        }else{
            boolean isAA = false;
            while(mid >= 0){
                if(isAA){
                    reverse = doCheck(s, mid, mid+1);
                    mid--;
                    isAA = false;
                }else{
                    reverse = doCheck(s, mid-1, mid+1);
                    isAA = true;
                }
                if(reverse >= 0) break;
            }
        }
        String ans = s;
        for(int i = reverse; i < n; i++){
            ans = s.substring(i,i+1) + ans;
        }
        return ans;
    }

    private int doCheck(String s, int l, int r){
        while(l >= 0 && s.charAt(l) == s.charAt(r)){
            l--;
            r++;
        }
        if(l >= 0) return -1;
        return r;
    }

    @Test
    public void test1() {
        Solution sol = new Solution();
        Assert.assertEquals("aaacecaaa", sol.shortestPalindrome("aacecaaa"));
    }

    @Test
    public void test2() {
        Solution sol = new Solution();
        Assert.assertEquals("dcbabcd", sol.shortestPalindrome("abcd"));
    }

    @Test
    public void test3() {
        Solution sol = new Solution();
        Assert.assertEquals("dcabbacd", sol.shortestPalindrome("abbacd"));
    }

    @Test
    public void test4() {
        Solution sol = new Solution();
        Assert.assertEquals("", sol.shortestPalindrome("Ã¥"));
    }
}
